import React from 'react';

const Navigation = ({ currentView, setCurrentView, currentUser, onLogout }) => {
  const navItems = [
    { id: 'dashboard', label: 'Dashboard', icon: '📊' },
    { id: 'groups', label: 'Groups', icon: '👥' },
    { id: 'add-expense', label: 'Add Expense', icon: '➕' },
    { id: 'settle-up', label: 'Settle Up', icon: '💰' },
    { id: 'admin', label: 'Admin', icon: '⚙️' },
  ];

  return (
    <nav className="bg-white shadow-lg">
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between">
          <div className="flex space-x-7">
            <div>
              <a href="#" className="flex items-center py-4 px-2">
                <span className="font-semibold text-gray-500 text-lg">Settlr</span>
              </a>
            </div>
            <div className="hidden md:flex items-center space-x-1">
              {navItems.map((item) => (
                <button
                  key={item.id}
                  onClick={() => setCurrentView(item.id)}
                  className={`py-4 px-2 text-gray-500 font-semibold hover:text-green-500 transition duration-300 ${
                    currentView === item.id ? 'text-green-500 border-b-4 border-green-500' : ''
                  }`}
                >
                  <span className="mr-1">{item.icon}</span>
                  {item.label}
                </button>
              ))}
            </div>
          </div>
          <div className="hidden md:flex items-center space-x-3">
            <span className="text-gray-700">Hello, {currentUser?.name || 'User'}</span>
            <button
              onClick={onLogout}
              className="py-2 px-2 font-medium text-gray-500 rounded hover:bg-green-500 hover:text-white transition duration-300"
            >
              Logout
            </button>
          </div>
          {/* Mobile menu button */}
          <div className="md:hidden flex items-center">
            <button className="outline-none mobile-menu-button">
              <svg
                className="w-6 h-6 text-gray-500 hover:text-green-500"
                fill="none"
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path d="M4 6h16M4 12h16M4 18h16"></path>
              </svg>
            </button>
          </div>
        </div>
      </div>
      {/* Mobile Menu */}
      <div className="hidden mobile-menu">
        <ul className="">
          {navItems.map((item) => (
            <li key={item.id}>
              <button
                onClick={() => setCurrentView(item.id)}
                className={`block text-sm px-2 py-4 text-white bg-green-500 font-semibold ${
                  currentView === item.id ? 'bg-green-600' : 'hover:bg-green-400'
                }`}
              >
                <span className="mr-1">{item.icon}</span>
                {item.label}
              </button>
            </li>
          ))}
          <li>
            <button
              onClick={onLogout}
              className="block text-sm px-2 py-4 text-white bg-red-500 hover:bg-red-400 font-semibold w-full text-left"
            >
              Logout
            </button>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navigation;
