import React, { useState, useEffect } from 'react';

const Admin = ({ currentUser }) => {
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalGroups: 0,
    totalExpenses: 0,
    totalAmount: 0
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [confirmations, setConfirmations] = useState({});

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const [usersRes, groupsRes, expensesRes] = await Promise.all([
        fetch('/api/users'),
        fetch('/api/expense-groups'),
        fetch('/api/expenses')
      ]);

      const users = usersRes.ok ? await usersRes.json() : [];
      const groups = groupsRes.ok ? await groupsRes.json() : [];
      const expenses = expensesRes.ok ? await expensesRes.json() : [];

      const totalAmount = expenses.reduce((sum, expense) => sum + expense.amount, 0);

      setStats({
        totalUsers: users.length,
        totalGroups: groups.length,
        totalExpenses: expenses.length,
        totalAmount
      });
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const showMessage = (text, type = 'success') => {
    setMessage({ text, type });
    setTimeout(() => setMessage(''), 3000);
  };

  const handleClearAllData = async () => {
    if (!confirmations.clearAll) {
      setConfirmations({ ...confirmations, clearAll: true });
      return;
    }

    setLoading(true);
    try {
      // Clear expenses first (due to foreign key constraints)
      await fetch('/api/expenses/clear-all', { method: 'DELETE' });

      // Clear groups
      await fetch('/api/expense-groups/clear-all', { method: 'DELETE' });

      // Clear users (except current user)
      const response = await fetch(`/api/users/clear-all-except/${currentUser.id}`, {
        method: 'DELETE'
      });

      if (response.ok) {
        showMessage('All data cleared successfully!', 'success');
        fetchStats();
      } else {
        showMessage('Failed to clear some data', 'error');
      }
    } catch (error) {
      showMessage('Error clearing data: ' + error.message, 'error');
    } finally {
      setLoading(false);
      setConfirmations({ ...confirmations, clearAll: false });
    }
  };

  const handleClearExpenses = async () => {
    if (!confirmations.clearExpenses) {
      setConfirmations({ ...confirmations, clearExpenses: true });
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('/api/expenses/clear-all', { method: 'DELETE' });
      if (response.ok) {
        showMessage('All expenses cleared successfully!', 'success');
        fetchStats();
      } else {
        showMessage('Failed to clear expenses', 'error');
      }
    } catch (error) {
      showMessage('Error clearing expenses: ' + error.message, 'error');
    } finally {
      setLoading(false);
      setConfirmations({ ...confirmations, clearExpenses: false });
    }
  };

  const handleClearGroups = async () => {
    if (!confirmations.clearGroups) {
      setConfirmations({ ...confirmations, clearGroups: true });
      return;
    }

    setLoading(true);
    try {
      // Clear expenses in groups first
      await fetch('/api/expenses/clear-all', { method: 'DELETE' });

      // Then clear groups
      const response = await fetch('/api/expense-groups/clear-all', { method: 'DELETE' });
      if (response.ok) {
        showMessage('All groups and related expenses cleared successfully!', 'success');
        fetchStats();
      } else {
        showMessage('Failed to clear groups', 'error');
      }
    } catch (error) {
      showMessage('Error clearing groups: ' + error.message, 'error');
    } finally {
      setLoading(false);
      setConfirmations({ ...confirmations, clearGroups: false });
    }
  };

  const handleResetDatabase = async () => {
    if (!confirmations.resetDB) {
      setConfirmations({ ...confirmations, resetDB: true });
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('/api/admin/reset-database', { method: 'POST' });
      if (response.ok) {
        showMessage('Database reset successfully!', 'success');
        fetchStats();
      } else {
        showMessage('Failed to reset database', 'error');
      }
    } catch (error) {
      showMessage('Error resetting database: ' + error.message, 'error');
    } finally {
      setLoading(false);
      setConfirmations({ ...confirmations, resetDB: false });
    }
  };

  const handleExportData = async () => {
    setLoading(true);
    try {
      const response = await fetch('/api/admin/export-data');
      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `settlr-backup-${new Date().toISOString().split('T')[0]}.json`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
        showMessage('Data exported successfully!', 'success');
      } else {
        showMessage('Failed to export data', 'error');
      }
    } catch (error) {
      showMessage('Error exporting data: ' + error.message, 'error');
    } finally {
      setLoading(false);
    }
  };

  const cancelConfirmation = (action) => {
    setConfirmations({ ...confirmations, [action]: false });
  };

  return (
    <div className="max-w-6xl mx-auto">
      <div className="bg-white rounded-lg shadow-md p-6">
        <h1 className="text-3xl font-bold text-gray-800 mb-6 flex items-center">
          <span className="mr-3">⚙️</span>
          Admin Panel
        </h1>

        {message && (
          <div className={`mb-6 p-4 rounded-lg ${
            message.type === 'success'
              ? 'bg-green-100 border border-green-400 text-green-700'
              : 'bg-red-100 border border-red-400 text-red-700'
          }`}>
            {message.text}
          </div>
        )}

        {/* Statistics Dashboard */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-blue-50 p-6 rounded-lg border border-blue-200">
            <div className="flex items-center">
              <div className="text-blue-600 text-2xl mr-4">👥</div>
              <div>
                <p className="text-blue-600 text-sm font-medium">Total Users</p>
                <p className="text-3xl font-bold text-blue-800">{stats.totalUsers}</p>
              </div>
            </div>
          </div>

          <div className="bg-green-50 p-6 rounded-lg border border-green-200">
            <div className="flex items-center">
              <div className="text-green-600 text-2xl mr-4">🏠</div>
              <div>
                <p className="text-green-600 text-sm font-medium">Total Groups</p>
                <p className="text-3xl font-bold text-green-800">{stats.totalGroups}</p>
              </div>
            </div>
          </div>

          <div className="bg-yellow-50 p-6 rounded-lg border border-yellow-200">
            <div className="flex items-center">
              <div className="text-yellow-600 text-2xl mr-4">📋</div>
              <div>
                <p className="text-yellow-600 text-sm font-medium">Total Expenses</p>
                <p className="text-3xl font-bold text-yellow-800">{stats.totalExpenses}</p>
              </div>
            </div>
          </div>

          <div className="bg-purple-50 p-6 rounded-lg border border-purple-200">
            <div className="flex items-center">
              <div className="text-purple-600 text-2xl mr-4">💰</div>
              <div>
                <p className="text-purple-600 text-sm font-medium">Total Amount</p>
                <p className="text-3xl font-bold text-purple-800">${stats.totalAmount.toFixed(2)}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Data Management Section */}
        <div className="mb-8">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">Data Management</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">

            {/* Export Data */}
            <div className="bg-blue-50 p-6 rounded-lg border border-blue-200">
              <h3 className="text-lg font-semibold text-blue-800 mb-2 flex items-center">
                <span className="mr-2">📤</span>
                Export Data
              </h3>
              <p className="text-blue-600 mb-4 text-sm">
                Download a backup of all your data as JSON file
              </p>
              <button
                onClick={handleExportData}
                disabled={loading}
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg transition-colors disabled:opacity-50"
              >
                {loading ? 'Exporting...' : 'Export Data'}
              </button>
            </div>

            {/* Clear Expenses */}
            <div className="bg-yellow-50 p-6 rounded-lg border border-yellow-200">
              <h3 className="text-lg font-semibold text-yellow-800 mb-2 flex items-center">
                <span className="mr-2">🗑️</span>
                Clear Expenses
              </h3>
              <p className="text-yellow-600 mb-4 text-sm">
                Remove all expenses while keeping users and groups
              </p>
              {confirmations.clearExpenses ? (
                <div className="space-x-2">
                  <button
                    onClick={handleClearExpenses}
                    disabled={loading}
                    className="bg-red-600 hover:bg-red-700 text-white px-3 py-2 rounded text-sm transition-colors disabled:opacity-50"
                  >
                    {loading ? 'Clearing...' : 'Confirm Clear'}
                  </button>
                  <button
                    onClick={() => cancelConfirmation('clearExpenses')}
                    className="bg-gray-500 hover:bg-gray-600 text-white px-3 py-2 rounded text-sm transition-colors"
                  >
                    Cancel
                  </button>
                </div>
              ) : (
                <button
                  onClick={handleClearExpenses}
                  className="bg-yellow-600 hover:bg-yellow-700 text-white px-4 py-2 rounded-lg transition-colors"
                >
                  Clear Expenses
                </button>
              )}
            </div>

            {/* Clear Groups */}
            <div className="bg-orange-50 p-6 rounded-lg border border-orange-200">
              <h3 className="text-lg font-semibold text-orange-800 mb-2 flex items-center">
                <span className="mr-2">🏠</span>
                Clear Groups
              </h3>
              <p className="text-orange-600 mb-4 text-sm">
                Remove all groups and their expenses (keeps users)
              </p>
              {confirmations.clearGroups ? (
                <div className="space-x-2">
                  <button
                    onClick={handleClearGroups}
                    disabled={loading}
                    className="bg-red-600 hover:bg-red-700 text-white px-3 py-2 rounded text-sm transition-colors disabled:opacity-50"
                  >
                    {loading ? 'Clearing...' : 'Confirm Clear'}
                  </button>
                  <button
                    onClick={() => cancelConfirmation('clearGroups')}
                    className="bg-gray-500 hover:bg-gray-600 text-white px-3 py-2 rounded text-sm transition-colors"
                  >
                    Cancel
                  </button>
                </div>
              ) : (
                <button
                  onClick={handleClearGroups}
                  className="bg-orange-600 hover:bg-orange-700 text-white px-4 py-2 rounded-lg transition-colors"
                >
                  Clear Groups
                </button>
              )}
            </div>

            {/* Reset Database */}
            <div className="bg-red-50 p-6 rounded-lg border border-red-200">
              <h3 className="text-lg font-semibold text-red-800 mb-2 flex items-center">
                <span className="mr-2">🔄</span>
                Reset Database
              </h3>
              <p className="text-red-600 mb-4 text-sm">
                Completely reset the database to initial state
              </p>
              {confirmations.resetDB ? (
                <div className="space-x-2">
                  <button
                    onClick={handleResetDatabase}
                    disabled={loading}
                    className="bg-red-600 hover:bg-red-700 text-white px-3 py-2 rounded text-sm transition-colors disabled:opacity-50"
                  >
                    {loading ? 'Resetting...' : 'Confirm Reset'}
                  </button>
                  <button
                    onClick={() => cancelConfirmation('resetDB')}
                    className="bg-gray-500 hover:bg-gray-600 text-white px-3 py-2 rounded text-sm transition-colors"
                  >
                    Cancel
                  </button>
                </div>
              ) : (
                <button
                  onClick={handleResetDatabase}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
                >
                  Reset Database
                </button>
              )}
            </div>
          </div>
        </div>

        {/* Danger Zone */}
        <div className="bg-red-50 border border-red-200 rounded-lg p-6">
          <h2 className="text-2xl font-semibold text-red-800 mb-4 flex items-center">
            <span className="mr-2">⚠️</span>
            Danger Zone
          </h2>
          <div className="bg-white p-4 rounded border border-red-300">
            <h3 className="text-lg font-semibold text-red-800 mb-2">Clear All Data</h3>
            <p className="text-red-600 mb-4 text-sm">
              This will permanently delete ALL users (except you), groups, and expenses. This action cannot be undone!
            </p>
            {confirmations.clearAll ? (
              <div className="space-x-2">
                <button
                  onClick={handleClearAllData}
                  disabled={loading}
                  className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors disabled:opacity-50"
                >
                  {loading ? 'Clearing All...' : 'YES, DELETE EVERYTHING'}
                </button>
                <button
                  onClick={() => cancelConfirmation('clearAll')}
                  className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded-lg transition-colors"
                >
                  Cancel
                </button>
              </div>
            ) : (
              <button
                onClick={handleClearAllData}
                className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors"
              >
                Clear All Data
              </button>
            )}
          </div>
        </div>

        {/* System Info */}
        <div className="mt-8 bg-gray-50 p-4 rounded-lg">
          <h3 className="text-lg font-semibold text-gray-700 mb-2">System Information</h3>
          <div className="text-sm text-gray-600 space-y-1">
            <p><strong>Current User:</strong> {currentUser?.name} ({currentUser?.email})</p>
            <p><strong>Admin Panel Version:</strong> 1.0.0</p>
            <p><strong>Last Updated:</strong> {new Date().toLocaleString()}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Admin;
