import React, { useState, useEffect } from 'react';

const SettleUp = ({ currentUser }) => {
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const [settlements, setSettlements] = useState([]);
  const [settledItems, setSettledItems] = useState(() => {
    // Load settled items from localStorage
    const saved = localStorage.getItem('settledItems');
    return saved ? JSON.parse(saved) : [];
  });
  const [loading, setLoading] = useState(false);

  const API_BASE = 'http://localhost:8080/api';

  useEffect(() => {
    if (currentUser) {
      fetchGroups();
    }
  }, [currentUser]);

  const fetchGroups = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE}/groups/user/${currentUser.id}`);
      if (response.ok) {
        const groupsData = await response.json();
        setGroups(groupsData);
      }
    } catch (error) {
      console.error('Error fetching groups:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchGroupExpenses = async (groupId) => {
    try {
      const response = await fetch(`${API_BASE}/expenses/group/${groupId}`);
      if (response.ok) {
        const expensesData = await response.json();
        setExpenses(expensesData);
        calculateSettlements(expensesData, groupId);
      }
    } catch (error) {
      console.error('Error fetching expenses:', error);
    }
  };

