    console.log('🔍 Calculating settlements for expenses:', expensesData);
    
    // Calculate who owes what to whom
    const balances = {};
    const settlements = [];

    // Initialize balances for all users who have expenses
    expensesData.forEach(expense => {
      console.log('💰 Processing expense:', {
        description: expense.description,
        amount: expense.amount,
        paidBy: expense.paidBy?.name,
        splitBetweenUsers: expense.splitBetweenUsers?.map(u => u.name)
      });
      
      if (expense.paidBy?.name && !balances[expense.paidBy.name]) {
        balances[expense.paidBy.name] = 0;
      }
      if (expense.splitBetweenUsers) {
        expense.splitBetweenUsers.forEach(user => {
          if (!balances[user.name]) {
            balances[user.name] = 0;
          }
        });
      }
    });

    console.log('📊 Initial balances:', balances);

    // Calculate net balances
    expensesData.forEach(expense => {
      const totalAmount = expense.amount;
      const splitCount = expense.splitBetweenUsers?.length || 1;
      const amountPerPerson = totalAmount / splitCount;

      console.log(`   Split: ${totalAmount} / ${splitCount} = ${amountPerPerson} per person`);

      // Person who paid gets credited
      if (expense.paidBy?.name) {
        balances[expense.paidBy.name] += totalAmount - amountPerPerson;
      }

      // Each person in split owes their share
      if (expense.splitBetweenUsers) {
        expense.splitBetweenUsers.forEach(user => {
          if (user.name !== expense.paidBy?.name) {
            balances[user.name] -= amountPerPerson;
          }
        });
      }
    });

    console.log('📊 Final balances:', balances);

    console.log('📊 Final balances:', balances);

    // Generate settlements
    const creditors = [];
    const debtors = [];

    Object.entries(balances).forEach(([user, balance]) => {
      if (balance > 0.01) {
        creditors.push({ user, amount: balance });
      } else if (balance < -0.01) {
        debtors.push({ user, amount: Math.abs(balance) });
      }
    });

    console.log('👤 Creditors (people who are owed):', creditors);
    console.log('👤 Debtors (people who owe):', debtors);

    // Create settlements
    creditors.forEach(creditor => {
      debtors.forEach(debtor => {
        if (creditor.amount > 0 && debtor.amount > 0) {
          const settlementAmount = Math.min(creditor.amount, debtor.amount);
          if (settlementAmount > 0.01) {
            const settlementKey = `${groupId}-${debtor.user}-${creditor.user}`;
            console.log(`💸 Settlement: ${debtor.user} → ${creditor.user}: ${settlementAmount}, key: ${settlementKey}`);
            
            // Only add if not already settled
            if (!settledItems.includes(settlementKey)) {
              console.log('   ✅ Adding settlement (not yet settled)');
              settlements.push({
                from: debtor.user,
                to: creditor.user,
                amount: settlementAmount,
                key: settlementKey
              });
            } else {
              console.log('   ⏭️ Skipping settlement (already marked as settled)');
            }
            creditor.amount -= settlementAmount;
            debtor.amount -= settlementAmount;
          }
        }
      });
    });

    console.log('✅ Final settlements to display:', settlements);
    setSettlements(settlements);
  };

  const handleGroupSelect = (group) => {
    setSelectedGroup(group);
    fetchGroupExpenses(group.id);
  };

  const markAsSettled = async (settlement) => {
    // Add to settled items and persist in localStorage
    const newSettledItems = [...settledItems, settlement.key];
    setSettledItems(newSettledItems);
    localStorage.setItem('settledItems', JSON.stringify(newSettledItems));
    
    // Remove from current settlements
    setSettlements(prev => prev.filter(s => s.key !== settlement.key));
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR'
    }).format(amount || 0);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 p-6">
        <div className="max-w-6xl mx-auto">
          <div className="bg-white rounded-xl shadow-lg p-8 text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
            <p className="text-gray-600">Loading groups...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 p-6">
      <div className="max-w-6xl mx-auto">
        <div className="bg-white rounded-xl shadow-lg p-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-6">💰 Settle Up</h1>

          {!selectedGroup ? (
            <div>
              <h2 className="text-xl font-semibold text-gray-700 mb-4">Select a Group to Settle</h2>
              {groups.length === 0 ? (
                <p className="text-gray-500">No groups found. Create a group first to start settling expenses.</p>
              ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                  {groups.map((group, index) => {
                    // Rotate between green, red, blue, violet
                    const borderColors = [
                      'border-green-400 hover:border-green-500',
                      'border-red-400 hover:border-red-500',
                      'border-blue-400 hover:border-blue-500',
                      'border-violet-400 hover:border-violet-500'
                    ];
                    const borderColor = borderColors[index % borderColors.length];
                    
                    return (
                      <div
                        key={group.id}
                        onClick={() => handleGroupSelect(group)}
                        className={`bg-white border-2 ${borderColor} rounded-lg p-4 cursor-pointer hover:shadow-md transition-all duration-200 transform hover:scale-105`}
                      >
                        <h3 className="text-lg font-semibold mb-2 text-gray-800">{group.name}</h3>
                        <p className="text-gray-500 text-sm">{group.description}</p>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>
          ) : (
            <div>
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-xl font-semibold text-gray-700">
                  Settlements for: {selectedGroup.name}
                </h2>
                <div className="flex gap-2">
                  {settledItems.some(item => item.startsWith(`${selectedGroup.id}-`)) && (
                    <button
                      onClick={() => {
                        // Clear settled items for this group
                        const filteredItems = settledItems.filter(item => !item.startsWith(`${selectedGroup.id}-`));
                        setSettledItems(filteredItems);
                        localStorage.setItem('settledItems', JSON.stringify(filteredItems));
                        // Recalculate settlements
                        fetchGroupExpenses(selectedGroup.id);
                      }}
                      className="bg-orange-500 hover:bg-orange-600 text-white px-4 py-2 rounded-lg transition-colors text-sm"
                      title="Clear settlement history for this group"
                    >
                      Clear History
                    </button>
                  )}
                  <button
                    onClick={() => {
                      setSelectedGroup(null);
                      setExpenses([]);
                      setSettlements([]);
                    }}
                    className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded-lg transition-colors"
                  >
                    ← Back to Groups
                  </button>
                </div>
              </div>

              {settlements.length === 0 ? (
                <div className="text-center py-8">
                  <div className="text-6xl mb-4">🎉</div>
                  <h3 className="text-xl font-semibold text-gray-700 mb-2">All Settled Up!</h3>
                  <p className="text-gray-500">No outstanding balances in this group.</p>
                </div>
              ) : (
                <div>
                  <h3 className="text-lg font-semibold text-gray-700 mb-4">
                    Outstanding Settlements ({settlements.length})
                  </h3>
                  <div className="space-y-4">
                    {settlements.map((settlement, index) => (
                      <div
                        key={index}
                        className="bg-yellow-50 border-l-4 border-yellow-400 p-4 rounded-r-lg"
                      >
                        <div className="flex items-center justify-between">
                          <div className="flex items-center space-x-4">
                            <div className="text-yellow-600">
                              <svg className="w-6 h-6" fill="currentColor" viewBox="0 0 20 20">
                                <path fillRule="evenodd" d="M4 4a2 2 0 00-2 2v4a2 2 0 002 2V6h10a2 2 0 00-2-2H4zm2 6a2 2 0 012-2h8a2 2 0 012 2v4a2 2 0 01-2 2H8a2 2 0 01-2-2v-4zm6 4a2 2 0 100-4 2 2 0 000 4z" clipRule="evenodd" />
                              </svg>
                            </div>
                            <div>
                              <p className="text-gray-800">
                                <span className="font-semibold">{settlement.from}</span> owes{' '}
                                <span className="font-semibold">{settlement.to}</span>
                              </p>
                              <p className="text-2xl font-bold text-green-600">
                                {formatCurrency(settlement.amount)}
                              </p>
                            </div>
                          </div>
                          <button
                            onClick={() => markAsSettled(settlement)}
                            className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg transition-colors"
                          >
                            Mark as Settled
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>

                  
                </div>
              )}

              {expenses.length > 0 && (
                <div className="mt-8">
                  <h3 className="text-lg font-semibold text-gray-700 mb-4">
                    Recent Expenses ({expenses.length})
                  </h3>
                  <div className="bg-gray-50 rounded-lg p-4">
                    <div className="space-y-2">
                      {expenses.slice(0, 5).map(expense => (
                        <div key={expense.id} className="flex justify-between items-center text-sm">
                          <span className="text-gray-600">{expense.description}</span>
                          <div className="text-right">
                            <div className="font-semibold">{formatCurrency(expense.amount)}</div>
                            <div className="text-gray-500 text-xs">
                              Paid by {expense.paidBy?.name}
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                    {expenses.length > 5 && (
                      <p className="text-gray-500 text-sm mt-2">
                        ...and {expenses.length - 5} more expenses
                      </p>
                    )}
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default SettleUp;
